vector<string> getNames(string list) { //separates all the names from each other

    vector<string> names = {};
    string currentName = "";
    for(int i = 0; i < list.size(); i++) {
        if(list.at(i) == ',' || list.at(i) == ':') {
            names.push_back(currentName);
            currentName = "";
            i++;
        }
        else if(list.at(i) == '.' && !(currentName == "Mr" || currentName == "Mrs" || currentName == "Ms")) {
            names.push_back(currentName);
            currentName = "";
            i++;
        }
        else
            currentName += list.at(i);
    }
    names.push_back(currentName);

    return names;

}

void checkSpelling(vector<string> names) { //compares each name to the names in the database

    cout << "Wrong Names:" << endl;
    for(int i = 0; i < names.size(); i++) {

        string currentName = names.at(i);
        int firstLetter = (int)(currentName.at(0));
        if(firstLetter < 97)
            firstLetter -= 65;
        else
            firstLetter -= 97;

        if(firstLetter < 26) {

            bool nameFound = false;
            for(int j = 0; j < nameDB[firstLetter].size(); j++) {
                if(currentName == nameDB[firstLetter][j]) {
                    nameFound = true;
                    break;
                }
            }
            if(!nameFound)
                cout << currentName << endl;

        }

    }
    cout << "\n";

}

